import React, { useState, useRef, useEffect, useCallback } from 'react'
import { useWeb3React } from '@web3-react/core'
import { Button, Text, Flex, AutoRenewIcon } from '@pancakeswap-libs/uikit'
import BigNumber from 'bignumber.js'
import { FarmWithStakedValue } from 'views/Farms/components/FarmCard/FarmCard'
import { getBalanceNumber } from 'utils/formatBalance'
import { useHarvest } from 'hooks/useHarvest'
import useComp from 'hooks/useStake'
import styled from 'styled-components'

import useI18n from 'hooks/useI18n'
import { usePriceCakeBusd, useToast } from 'state/hooks'
import { useCountUp } from 'react-countup'

import Tooltip from '../../Tooltip/Tooltip'
import { ActionContainer, ActionTitles, Title, Subtle, ActionContent, Earned, Staked } from './styles'

const BalanceAndCompound = styled.div`
  display: inline;
  align-items: center;
  justify-content: space-between;
  flex-direction: column;
`

const HarvestAction: React.FunctionComponent<FarmWithStakedValue> = ({ pid, userData }) => {
  const earningsBigNumber = userData ? new BigNumber(userData.earnings) : null
  const cakePrice = usePriceCakeBusd()
  let earnings = null
  let earningsBusd = 0
  let displayBalance = '0'

  if (earningsBigNumber) {
    earnings = getBalanceNumber(earningsBigNumber)
    earningsBusd = new BigNumber(earnings).multipliedBy(cakePrice).toNumber()
    displayBalance = earningsBusd.toFixed(2)
  }
  const [pendingTx, setPendingTx] = useState(false)
  const { account } = useWeb3React()
  const { onReward } = useHarvest(pid)
  const TranslateString = useI18n()
  const { onStake } = useComp(9)

  const { countUp, update } = useCountUp({
    end: earnings,
    duration: 1,
    decimals: 5,
  })
  const updateValue = useRef(update)

  useEffect(() => {
    updateValue.current(earnings)
  }, [earnings, updateValue])

  const { toastSuccess, toastError } = useToast()

  const harvestFarms = useCallback(async () => {
    setPendingTx(true)
    try {
      await onReward()
      toastSuccess('Done', `You harvested ${countUp} KYRIOS.`)
    } catch (e) {
      console.error(e)
      toastError('Error', e?.message)
      // TODO: find a way to handle when the user rejects transaction or it fails
    } finally {
      setPendingTx(false)
    }
  }, [onReward, toastSuccess, toastError, countUp])

  const compoundFarms = useCallback(async () => {
    setPendingTx(true)
    try {
      await onStake(earnings.toString())
      toastSuccess('Success', `You have compounded ${countUp} KYRIOS.`)
    } catch (e) {
      console.error(e)
      toastError('Error', e?.message)
      // TODO: find a way to handle when the user rejects transaction or it fails
    } finally {
      setPendingTx(false)
    }
  }, [onStake, toastSuccess, toastError, earnings, countUp])

  return (
    <ActionContainer>
      <ActionTitles>
        <Flex mb="8px" alignItems="center" justifyContent="center">
          <img
            src="data:image/jpeg;base64,
"
            alt="Kyrios_Logo"
            width="10%"
          />{' '}
        </Flex>
        <Text color='#7f7f7f' fontSize="12px">EARNED:</Text>
      </ActionTitles>
      <ActionContent>
        <Flex alignItems="center" justifyContent="center">
          <Earned>{countUp}</Earned>
          <Staked>(${displayBalance})</Staked>
        </Flex>
      </ActionContent>
      <BalanceAndCompound>
        {pid === 9 ? (
          // {false ?
          <Button
            width="100%"
            disabled={earnings === 0 || pendingTx || !account}
            endIcon={pendingTx ? <AutoRenewIcon spin color="currentColor" /> : null}
            marginBottom="0"
            marginTop="13px"
            ml="4px"
            onClick={compoundFarms}
          >
            {TranslateString(999, 'Compound')}
          </Button>
        ) : null}
        {pid === 9 ? null : ( // {false ?
          <Button
            mt="54px"
            width="100%"
            disabled={!earnings || pendingTx || !account}
            endIcon={pendingTx ? <AutoRenewIcon spin color="currentColor" /> : null}
            onClick={harvestFarms}
            ml="4px"
          >
            {TranslateString(562, 'Harvest')}
          </Button>
        )}
      </BalanceAndCompound>
      {pid === 9 ? (
        <Button
          mt="12px"
          width="100%"
          disabled={!earnings || pendingTx || !account}
          endIcon={pendingTx ? <AutoRenewIcon spin color="currentColor" /> : null}
          onClick={harvestFarms}
          ml="4px"
        >
          {TranslateString(562, 'Harvest')}
        </Button>
      ) : null}
    </ActionContainer>
  )
}

export default HarvestAction
